import React,{useEffect,useState} from 'react'
import { Route,Routes} from "react-router-dom";
import Navlink from './Navlink';
import Invoice from './Invoice';
import About from './About';
import Service from './Service'; 
import SignIn from './SignIn';
import SignUp from './SignUP'
import '../css/App.css'
import Footer from './Footer';
import Home from './Home';

function App () {


  const [currentUser, setCurrentUser] = useState(null)

  const changeUser = (user) => {
    setCurrentUser(user)
  }

  // Set developer as user , fetch user from database https://9292/developers

  const [developers, setDevelopers] = useState([])

  useEffect(() => {
    fetch('http://localhost:9292/developers')
      .then(response => response.json())
      .then(data => setDevelopers(data))
  }, [])


  // Client details 

   const [client, setClient] = useState({
        name: '',
        email: '',
        phone_number: '',
        location: '',
        developer_id :dev.id
    })
    function handleSubmit(e) {
        e.preventDefault()
        fetch('http://localhost:9292/clients', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(client)
        })
        .then(response => response.json())
        .then(data => console.log(data))
        setClient({
            name: '',
            email: '',
            phone_number: '',
            location: '',
        })
    }

    const handleChange = (e) => {
        setClient({
            ...client,
            [e.target.name]: e.target.value
        })
    }

  return (

    <div>

     < Navlink/>
     < Footer />
      <Routes>

        <Route path='/home' element={<Home />} ></Route>
        <Route path="/" exact component={Home} />
        <Route path='/services' element={<Service />} ></Route>
        <Route path='/about' element={<About />} ></Route>

         <Route  path='/home' element={<Home />} ></Route>
        <Route  path='/' element={<Home />} ></Route>
        <Route  path='/about' element={<About />} ></Route>

        <Route  path='/service' element={<Service />} ></Route>

        <Route path="/signin" element={<SignIn changeUser={changeUser} />} />
        <Route path="/signup" element={<SignUp />} />      
        {
          developers.map(dev => {
            return <Route key={dev.id} path={`/${dev.id}/invoice`} element={<Invoice dev={dev}  />} />
          }
          )
        }
      </Routes>  
    </div>
  )
}

export default App
